mapper.select:
SELECT /* common.getDbConnList */ USER_UID, DB_CONN_UID, DB_CONN_NM, DB_TYPE_CD, USERNAME, PASSWORD, HOSTNAME, PORT ,DB_CONN_TYPE_CD, DB_NM, DB_CONN_ROLE_CD ,DB_CONN_NM as tree_order ,'icon-db' as "iconCls" FROM TB_DB_CONN_I WHERE 1 = 1
AND DB_CONN_UID = #{DB_CONN_UID}
AND USER_UID = #{USER_UID}
ORDER BY tree_order

SELECT /* common.getDbConnTreeList */ DB_CONN_UID id, DB_CONN_NM text, USER_UID, DB_CONN_UID, DB_CONN_NM, DB_TYPE_CD, USERNAME, PASSWORD, HOSTNAME, PORT ,DB_CONN_TYPE_CD, DB_NM, DB_CONN_ROLE_CD ,'false' LEAF FROM TB_DB_CONN_I WHERE 1 = 1
AND DB_CONN_UID = #{DB_CONN_UID}
ORDER BY REG_DTM
