mapper.select:
		SELECT /* projwork.getEmpList */
		       EMP_ID, EMP_NO, EMAIL, PASSWD, EMP_NM, MNG_LVL, ENT_DT, REG_DTM, MOD_DTM
		  FROM TB_EMP
		 WHERE 1 = 1

		   AND EMP_ID = #{EMP_ID}


			AND EMP_NM LIKE CONCAT('%', #{EMP_NM}, '%')


			AND EMAIL = #{EMAIL}
		    AND PASSWD = #{PASSWD}

         ORDER BY EMP_ID DESC

		limit #{start}, #{limit}

SELECT /* employ.getEmpListCnt */
COUNT(*)
FROM TB_EMP
WHERE 1 = 1

	AND EMP_ID = #{EMP_ID}


	AND EMAIL = #{EMAIL}
	AND PASSWD = #{PASSWD}
