mapper.select:
SELECT /* project.getProjList */ A.PROJ_ID, A.PROJ_NM, A.PROJ_STAT_CD, A.PROJ_START_DT, A.PROJ_END_DT ,MAX(DATE_FORMAT(A.REG_DTM,'%Y-%m-%d %H:%i:%s')) REG_DTM ,MAX(DATE_FORMAT(A.MOD_DTM,'%Y-%m-%d %H:%i:%s')) MOD_DTM ,COUNT(B.EMP_ID) PROJ_EMP_CNT FROM TB_PROJ A LEFT OUTER JOIN TB_PROJ_EMP B ON A.PROJ_ID = B.PROJ_ID WHERE 1 = 1
AND A.PROJ_ID = #{PROJ_ID}
AND A.PROJ_NM LIKE CONCAT('%', #{PROJ_NM}, '%')
GROUP BY A.PROJ_ID, A.PROJ_NM, A.PROJ_STAT_CD, A.PROJ_START_DT, A.PROJ_END_DT ORDER BY A.PROJ_ID DESC
limit #{start}, #{limit}

SELECT /* project.getProjListCnt */ COUNT(*) FROM TB_PROJ WHERE 1 = 1
AND PROJ_ID = #{PROJ_ID}
AND PROJ_NM LIKE CONCAT('%', #{PROJ_NM}, '%')

SELECT /* project.getProjEmpList */ A.PROJ_ID, A.EMP_ID, B.EMP_NO, B.EMP_NM, B.MNG_LVL, A.PROJ_ROLE, A.EMP_PROJ_START_DT, A.EMP_PROJ_END_DT ,A.EMP_PROJ_LVL, A.EMP_PRICE, A.BASIC_QUAL, A.JOB_SKILL, A.OFFCE_ATTUDE FROM TB_PROJ_EMP A INNER JOIN TB_EMP B ON A.EMP_ID = B.EMP_ID WHERE 1 = 1
AND PROJ_ID = #{PROJ_ID}
ORDER BY EMP_NM DESC
